import axios from "axios";
import React, { useEffect, useState } from "react";
import Rightside from "../Home-Slider-Navbar/RightSide";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";

const Course = () => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  let [data, updatedata] = useState([]);
  let [sdata, updatesdata] = useState({
    id: "",
    name: "",
    fees: null,
    duration: "",
    comments: "",
  });
  let [product, updateproduct] = useState({
    id: "",
    name: "",
    fees: null,
    duration: "",
    comments: "",
  });
  async function showDisplal() {
    let r = await axios.get("https://princestudentapi.onrender.com/Course//");
    updatedata(r.data);
  }
  useEffect(() => {
    showDisplal();
  });
  const change = (e) => {
    updateproduct({ ...product, [e.target.name]: e.target.value });
  };
  return (
    <>
      <div>
        {sdata.id} {sdata.name} {sdata.fees} {sdata.duration} {sdata.comments}
      </div>
      <div className="px-3">
        <p className="text-white fs-4">Our Courses</p>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <form onSubmit={(e)=>{
            e.preventDefault();
            async function addpro()
            {
                let r=await axios.post('http://tanveerpp.pythonanywhere.com/product/',product);
                if(r.status===201)
                {
                    alert('product sucessfully added')
                }
            }
            async function updatepro()
            {
                let r=await axios.put(`http://tanveerpp.pythonanywhere.com/product/${product.id}/`,product);
                if(r.status===200)
                {
                    alert('product sucessfully updated')
                }
            }
            if(product.id==="")
            {
                addpro();
            }
            else{
                updatepro();
            }
        }}>
            Name<input type="text" name="name" value={product.name} onChange={change} className="form-control" />
            Price<input type="number" name="price" value={product.price} onChange={change} className="form-control" />
            Category<input type="text" name="cat" value={product.cat} onChange={change} className="form-control" />
            Company<input type="text" name="cmp" value={product.cmp} onChange={change} className="form-control" />
            {product.id===''?<button className='btn btn-primary'>Add Product</button>:<button className='btn btn-primary'>Update Product</button>}
        </form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
        <table className="table  caption-top text-light rounded mt-2">
          <thead>
            <tr>
              <th scope="col">COURSE NAME</th>
              <th scope="col"> FEES</th>
              <th scope="col">DURATION</th>
              <th scope="col">COMMENTS</th>
            </tr>
          </thead>

          <tbody>
            {data.map((v) => {
              return (
                <tr key={v.id}>
                  <td>{v.name}</td>
                  <td>{v.fees}</td>
                  <td>{v.duration}</td>
                  <td>{v.comments}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <Rightside />
    </>
  );
};
export default Course;
